        // دالة لاستخراج الكلمات الرئيسية
        function extractKeywords(question) {
            var stopWords = ["و", "في", "من", "على", "إلى", "عن", "أن", "ل", "ب"];
            return question
                .split(" ")
                .filter(word => word.length > 2 && !stopWords.includes(word));
        }

        // دالة للبحث في المقالات مع الأخذ بالاعتبار تكرار السؤال
        function searchArticles(keywords, repeatCount) {
            var possibleMatches = [];

            articles.forEach(article => {
                var matchCount = 0;

                keywords.forEach(keyword => {
                    if (article.toLowerCase().includes(keyword.toLowerCase())) {
                        matchCount++;
                    }
                });

                if (matchCount > 0) {
                    possibleMatches.push({ article, matchCount });
                }
            });

            // ترتيب المقالات بناءً على عدد المطابقات واختيار المقال التالي عند تكرار السؤال
            possibleMatches.sort((a, b) => b.matchCount - a.matchCount);
            var matchIndex = repeatCount % possibleMatches.length;

            return possibleMatches.length > 0 ? possibleMatches[matchIndex].article : "ملاحضة : أكتب سؤالك بشكل أفضل أو اكتب الكلمة التي تبحث عنها";
        }

