        function getBotResponse(userInput) {
            var matches = Object.keys(responses).filter(key => userInput.toLowerCase().includes(key));
            var responseMessages = [];

            if (matches.length === 0) {
                matches = ["افتراضي"];
            }

            matches.forEach(match => {
                if (!questionCount[match]) {
                    questionCount[match] = 0;
                }

                if (questionCount[match] < 4) {
                    var responseArray = responses[match];
                    var response = responseArray[questionCount[match] % responseArray.length];
                    responseMessages.push(response);
                    questionCount[match]++;
                }
            });

            if (responseMessages.length > 0) {
                return responseMessages.join(" ");
            } else {
                return repeatedMessage;
            }
        }
        
        
        
        
       

