        // دالة للتأكد من الضغط على زر "Enter"
        function checkEnter(event) {
            if (event.key === "Enter") {
                sendMessage();
            }
        }

        // دالة لتحويل النص إلى كلام
        function speakText() {
            var chatBox = document.getElementById("chat-box");
            var botMessages = chatBox.getElementsByClassName("bot");
            var latestBotMessage = botMessages[botMessages.length - 1].textContent;

            var utterance = new SpeechSynthesisUtterance(latestBotMessage);
            window.speechSynthesis.speak(utterance);
        }
